import React from 'react';

function RouteInstructions({ routeStats }) {
  if (!routeStats) return null;

  return (
    <div className="route-instructions">
      <h3>üìç Route Summary</h3>
      
      <div className="route-summary">
        <div className="summary-item">
          <span className="summary-label">Distance:</span>
          <span className="summary-value">{routeStats.distance_km.toFixed(2)} km</span>
        </div>
        <div className="summary-item">
          <span className="summary-label">Est. Time:</span>
          <span className="summary-value">{Math.round(routeStats.time_min)} mins</span>
        </div>
        {routeStats.risk && (
          <div className="summary-item">
            <span className="summary-label">Safety Level:</span>
            <span className="summary-value">{(routeStats.risk * 100).toFixed(0)}%</span>
          </div>
        )}
      </div>
    </div>
  );
}

export default RouteInstructions;
