import React, { useState } from 'react';
import { FaShieldAlt, FaRoute, FaDownload, FaFolder } from 'react-icons/fa';

function Sidebar({ 
  startPoint, 
  endPoint, 
  routeStats, 
  onFindRoute, 
  onClearRoute, 
  loading,
  savedLocations,
  onLoadLocation,
  onDownloadLocation,
  safeHavens
}) {
  const [showSaved, setShowSaved] = useState(false);
  const [showDownload, setShowDownload] = useState(false);
  const [downloadLocation, setDownloadLocation] = useState('');
  const [downloadRadius, setDownloadRadius] = useState(5000);

  const handleDownload = () => {
    if (downloadLocation) {
      onDownloadLocation(downloadLocation, downloadRadius);
      setShowDownload(false);
      setDownloadLocation('');
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <FaShieldAlt size={40} />
        <h1>SHEild-X</h1>
        <p>Safe Route Planning for Women</p>
      </div>

      {/* DOWNLOAD SECTION */}
      <div className="sidebar-section">
        <h3><FaDownload /> Download New Location</h3>
        <button 
          onClick={() => setShowDownload(!showDownload)} 
          className="toggle-btn"
        >
          {showDownload ? 'Cancel' : 'Download New Area'}
        </button>
        
        {showDownload && (
          <div className="download-form">
            <input
              type="text"
              placeholder="Enter city name (e.g., Coimbatore, India)"
              value={downloadLocation}
              onChange={(e) => setDownloadLocation(e.target.value)}
            />
            <select 
              value={downloadRadius} 
              onChange={(e) => setDownloadRadius(Number(e.target.value))}
            >
              <option value={2000}>2 km radius</option>
              <option value={5000}>5 km radius</option>
              <option value={10000}>10 km radius</option>
              <option value={20000}>20 km radius</option>
            </select>
            <button 
              onClick={handleDownload} 
              disabled={!downloadLocation || loading}
            >
              {loading ? 'Downloading...' : 'Download'}
            </button>
          </div>
        )}
      </div>

      {/* SAVED LOCATIONS */}
      <div className="sidebar-section">
        <h3><FaFolder /> Saved Locations</h3>
        <button 
          onClick={() => setShowSaved(!showSaved)} 
          className="toggle-btn"
        >
          {showSaved ? 'Hide' : 'Show'} Locations
        </button>
        
        {showSaved && (
          <div className="saved-list">
            {savedLocations.length === 0 ? (
              <p>No saved locations yet</p>
            ) : (
              savedLocations.map((loc, idx) => (
                <div key={idx} className="saved-item">
                  <span>{loc.name}</span>
                  <button onClick={() => onLoadLocation(loc.file)}>
                    Load
                  </button>
                </div>
              ))
            )}
          </div>
        )}
      </div>

      {/* ROUTE OPTIONS - ONLY SAFEST MODE */}
      <div className="sidebar-section">
        <h3><FaRoute /> Route Options</h3>
        
        {/* Simple indicator that we're using safest route */}
        <div className="mode-indicator">
          <span className="safest-badge">üõ°Ô∏è Safest Route Only</span>
          <p className="mode-description">Always finding the safest path for you</p>
        </div>

        {/* Selected points info */}
        <div className="points-info">
          {startPoint && (
            <div className="point">
              <span className="green-dot"></span>
              Start Selected
            </div>
          )}
          {endPoint && (
            <div className="point">
              <span className="red-dot"></span>
              Destination Selected
            </div>
          )}
        </div>

        {/* Action buttons */}
        <div className="action-buttons">
          <button 
            onClick={onFindRoute} 
            disabled={!startPoint || !endPoint || loading}
          >
            {loading ? 'Finding Safest Route...' : 'Find Safest Route'}
          </button>
          <button onClick={onClearRoute} className="secondary">
            Clear
          </button>
        </div>
      </div>

      {/* Route Statistics */}
      {routeStats && (
        <div className="sidebar-section stats">
          <h3>Route Statistics</h3>
          
          <div className="stat-item">
            <span>Distance:</span>
            <strong>{routeStats.distance_km.toFixed(2)} km</strong>
          </div>
          
          <div className="stat-item">
            <span>Est. Time:</span>
            <strong>{Math.round(routeStats.time_min)} mins</strong>
          </div>
          
          {routeStats.risk && (
            <div className="stat-item">
              <span>Safety Level:</span>
              <strong>{(routeStats.risk * 100).toFixed(0)}% Safe</strong>
            </div>
          )}
        </div>
      )}

      {/* Safe Havens */}
      {safeHavens && safeHavens.length > 0 && (
        <div className="sidebar-section">
          <h4>Safe Havens Nearby</h4>
          <div className="havens-summary">
            <div className="haven-stat">
              <span className="blue-dot"></span>
              Police: {safeHavens.filter(h => h.type === 'police').length}
            </div>
            <div className="haven-stat">
              <span className="red-dot"></span>
              Hospitals: {safeHavens.filter(h => h.type === 'hospital').length}
            </div>
          </div>
        </div>
      )}

      <div className="sidebar-footer">
        <p>Click map to select points</p>
        <p className="note">üõ°Ô∏è Always finding the safest route for you</p>
      </div>

      {/* Add this CSS to your styles.css file */}
      <style jsx>{`
        .mode-indicator {
          background: rgba(255, 255, 255, 0.15);
          border-radius: 8px;
          padding: 12px;
          margin: 10px 0;
          text-align: center;
        }
        
        .safest-badge {
          display: inline-block;
          background: #4CAF50;
          color: white;
          padding: 6px 12px;
          border-radius: 20px;
          font-weight: bold;
          font-size: 14px;
          margin-bottom: 8px;
        }
        
        .mode-description {
          font-size: 13px;
          opacity: 0.9;
          margin: 0;
        }
      `}</style>
    </div>
  );
}

export default Sidebar;
