import React, { useState } from 'react';
import { FaShieldAlt, FaRoute, FaClock, FaMapMarkedAlt, FaDownload, FaFolder, FaBalanceScale } from 'react-icons/fa';

function Sidebar({ 
  startPoint, 
  endPoint, 
  routeStats, 
  mode, 
  setMode, 
  onFindRoute, 
  onClearRoute, 
  loading,
  savedLocations,
  onLoadLocation,
  onDownloadLocation,
  safeHavens,
  riskWeight,           // NEW: risk preference from user
  setRiskWeight         // NEW: function to update risk preference
}) {
  const [showSaved, setShowSaved] = useState(false);
  const [showDownload, setShowDownload] = useState(false);
  const [downloadLocation, setDownloadLocation] = useState('');
  const [downloadRadius, setDownloadRadius] = useState(5000);
  const [selectedMode, setSelectedMode] = useState(mode);

  const handleModeChange = (newMode) => {
    setSelectedMode(newMode);
    setMode(newMode);
  };

  const handleDownload = () => {
    if (downloadLocation) {
      onDownloadLocation(downloadLocation, downloadRadius);
      setShowDownload(false);
      setDownloadLocation('');
    }
  };

  // Get risk level description
  const getRiskDescription = (weight) => {
    if (weight < 0.3) return "üîµ Speed‰ºòÂÖà (Prioritize Speed)";
    if (weight < 0.5) return "üü¢ Slightly Safe";
    if (weight < 0.7) return "üü° Balanced";
    if (weight < 0.9) return "üü† Quite Safe";
    return "üî¥ Very Safe";
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <FaShieldAlt size={40} />
        <h1>SHEild-X</h1>
        <p>Safe Route Planning for Women</p>
      </div>

      {/* DOWNLOAD SECTION */}
      <div className="sidebar-section">
        <h3><FaDownload /> Download New Location</h3>
        <button 
          onClick={() => setShowDownload(!showDownload)} 
          className="toggle-btn"
        >
          {showDownload ? 'Cancel' : 'Download New Area'}
        </button>
        
        {showDownload && (
          <div className="download-form">
            <input
              type="text"
              placeholder="Enter city name (e.g., Coimbatore, India)"
              value={downloadLocation}
              onChange={(e) => setDownloadLocation(e.target.value)}
            />
            <select 
              value={downloadRadius} 
              onChange={(e) => setDownloadRadius(Number(e.target.value))}
            >
              <option value={2000}>2 km radius</option>
              <option value={5000}>5 km radius</option>
              <option value={10000}>10 km radius</option>
              <option value={20000}>20 km radius</option>
            </select>
            <button 
              onClick={handleDownload} 
              disabled={!downloadLocation || loading}
            >
              {loading ? 'Downloading...' : 'Download'}
            </button>
          </div>
        )}
      </div>

      {/* SAVED LOCATIONS */}
      <div className="sidebar-section">
        <h3><FaFolder /> Saved Locations</h3>
        <button 
          onClick={() => setShowSaved(!showSaved)} 
          className="toggle-btn"
        >
          {showSaved ? 'Hide' : 'Show'} Locations
        </button>
        
        {showSaved && (
          <div className="saved-list">
            {savedLocations.length === 0 ? (
              <p>No saved locations yet</p>
            ) : (
              savedLocations.map((loc, idx) => (
                <div key={idx} className="saved-item">
                  <span>{loc.name}</span>
                  <button onClick={() => onLoadLocation(loc.file)}>
                    Load
                  </button>
                </div>
              ))
            )}
          </div>
        )}
      </div>

      {/* ROUTE OPTIONS - USER CONTROLLED */}
      <div className="sidebar-section">
        <h3><FaRoute /> Route Options</h3>
        
        {/* Mode selector */}
        <div className="mode-selector">
          <button
            className={selectedMode === 'user_defined' ? 'active' : ''}
            onClick={() => handleModeChange('user_defined')}
          >
            üë§ User Defined
          </button>
          <button
            className={selectedMode === 'shortest' ? 'active' : ''}
            onClick={() => handleModeChange('shortest')}
          >
            ‚ö° Shortest
          </button>
        </div>

        {/* USER RISK PREFERENCE SLIDER - THIS IS THE KEY! */}
        {selectedMode === 'user_defined' && (
          <div className="risk-slider-container">
            <label>
              <FaBalanceScale /> Your Safety Preference
            </label>
            <div className="slider-labels">
              <span>‚ö° Fast</span>
              <span>‚öñÔ∏è Balance</span>
              <span>üõ°Ô∏è Safe</span>
            </div>
            <input
              type="range"
              min="0.1"
              max="1.0"
              step="0.1"
              value={riskWeight}
              onChange={(e) => setRiskWeight(parseFloat(e.target.value))}
              className="risk-slider"
            />
            <div className="risk-display">
              {getRiskDescription(riskWeight)}
              <span className="risk-value">({Math.round(riskWeight * 100)}% Safe / {Math.round((1-riskWeight) * 100)}% Fast)</span>
            </div>
          </div>
        )}

        {/* Selected points info */}
        <div className="points-info">
          {startPoint && (
            <div className="point">
              <span className="green-dot"></span>
              Start: {startPoint.lat.toFixed(4)}, {startPoint.lon.toFixed(4)}
            </div>
          )}
          {endPoint && (
            <div className="point">
              <span className="red-dot"></span>
              End: {endPoint.lat.toFixed(4)}, {endPoint.lon.toFixed(4)}
            </div>
          )}
        </div>

        {/* Action buttons */}
        <div className="action-buttons">
          <button 
            onClick={onFindRoute} 
            disabled={!startPoint || !endPoint || loading}
          >
            {loading ? 'Finding...' : 'Find Route'}
          </button>
          <button onClick={onClearRoute} className="secondary">
            Clear
          </button>
        </div>
      </div>

      {/* Route Statistics */}
      {routeStats && (
        <div className="sidebar-section stats">
          <h3><FaMapMarkedAlt /> Route Statistics</h3>
          
          <div className="stat-item">
            <span>Distance:</span>
            <strong>{routeStats.distance_km.toFixed(2)} km</strong>
          </div>
          
          <div className="stat-item">
            <span>Est. Time:</span>
            <strong>{Math.round(routeStats.time_min)} mins</strong>
          </div>
          
          {routeStats.risk && (
            <div className="stat-item">
              <span>Risk Level:</span>
              <strong>{(routeStats.risk * 100).toFixed(0)}%</strong>
            </div>
          )}

          {routeStats.used_weight && (
            <div className="stat-item">
              <span>Your Preference:</span>
              <strong>{Math.round(routeStats.used_weight * 100)}% Safe</strong>
            </div>
          )}
        </div>
      )}

      {/* Safe Havens */}
      {safeHavens && safeHavens.length > 0 && (
        <div className="sidebar-section">
          <h4>Safe Havens Nearby</h4>
          <div className="havens-summary">
            <div className="haven-stat">
              <span className="blue-dot"></span>
              Police: {safeHavens.filter(h => h.type === 'police').length}
            </div>
            <div className="haven-stat">
              <span className="red-dot"></span>
              Hospitals: {safeHavens.filter(h => h.type === 'hospital').length}
            </div>
          </div>
        </div>
      )}

      <div className="sidebar-footer">
        <p>Click map to select points</p>
        <p className="note">‚ö° Fast vs üõ°Ô∏è Safe - You decide!</p>
      </div>
    </div>
  );
}

export default Sidebar;
