import React from 'react';

function RouteInstructions({ routeStats, instructions }) {
  if (!instructions) return null;

  return (
    <div className="route-instructions">
      <h3>üìç Turn-by-Turn Directions</h3>
      
      <div className="route-summary">
        <div>Distance: {routeStats?.distance_km.toFixed(2)} km</div>
        <div>Time: {Math.round(routeStats?.time_min)} mins</div>
        <div>Risk: {(routeStats?.risk * 100).toFixed(0)}%</div>
      </div>
      
      <div className="instructions-list">
        {instructions.map((step, index) => (
          <div key={index} className="instruction-step">
            <div className="step-number">{step.step}</div>
            <div className="step-content">
              <div>{step.instruction}</div>
              {step.distance > 0 && (
                <div className="step-distance">{step.distance} m</div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RouteInstructions;
