const findRoute = async () => {
  if (!startPoint || !endPoint) { 
    alert('Please select start and end points'); 
    return; 
  }

  setLoading(true);
  try {
    // Prepare request data
    const requestData = {
      start: {
        lat: parseFloat(startPoint.lat),
        lon: parseFloat(startPoint.lon || startPoint.lng)
      },
      end: {
        lat: parseFloat(endPoint.lat),
        lon: parseFloat(endPoint.lon || endPoint.lng)
      },
      mode: mode,
      risk_weight: riskWeight
    };

    console.log('Sending route request:', requestData);

    // Make API call
    const res = await axios.post(`${API_BASE}/route-with-instructions`, requestData);
    
    console.log('Route response:', res.data);

    if (res.data.error) {
      alert(`Error: ${res.data.error}`);
      return;
    }

    // Update state with route data
    setRoute(res.data.path);
    setRouteStats({
      ...res.data.statistics,
      used_weight: riskWeight
    });
    setRouteInstructions(res.data.instructions);
    setShowInstructions(true);
    
  } catch (error) {
    console.error('Route error:', error);
    
    // Handle different error types
    if (error.response) {
      // Server responded with error
      alert(`Server error: ${error.response.data.error || 'Unknown error'}`);
    } else if (error.request) {
      // Request made but no response
      alert('No response from server. Is backend running?');
    } else {
      // Something else happened
      alert('Error finding route: ' + error.message);
    }
  }
  setLoading(false);
};
