import React, { useState } from 'react';
import { FaShieldAlt, FaRoute } from 'react-icons/fa';

function Sidebar({ startPoint, endPoint, routeStats, mode, setMode, onFindRoute, onClearRoute, loading }) {
  const [selectedMode, setSelectedMode] = useState(mode);

  const handleModeChange = (newMode) => {
    setSelectedMode(newMode);
    setMode(newMode);
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <FaShieldAlt size={40} />
        <h1>SHEild-X</h1>
        <p>Safe Route Planning for Women</p>
      </div>

      <div className="sidebar-section">
        <h3><FaRoute /> Route Options</h3>
        <div className="mode-selector">
          <button 
            className={selectedMode === 'safest' ? 'active' : ''} 
            onClick={() => handleModeChange('safest')}
          >
            Safest
          </button>
          <button 
            className={selectedMode === 'balanced' ? 'active' : ''} 
            onClick={() => handleModeChange('balanced')}
          >
            Balanced
          </button>
          <button 
            className={selectedMode === 'shortest' ? 'active' : ''} 
            onClick={() => handleModeChange('shortest')}
          >
            Shortest
          </button>
        </div>

        <div className="points-info">
          {startPoint && <div>‚úÖ Start selected</div>}
          {endPoint && <div>üìç Destination selected</div>}
        </div>

        <div className="action-buttons">
          <button 
            onClick={onFindRoute} 
            disabled={!startPoint || !endPoint || loading}
          >
            {loading ? 'Finding...' : 'Find Route'}
          </button>
          <button onClick={onClearRoute} className="secondary">
            Clear
          </button>
        </div>
      </div>

      {routeStats && (
        <div className="sidebar-section">
          <h3>Route Statistics</h3>
          <div>Distance: {routeStats.distance_km.toFixed(2)} km</div>
          <div>Time: {Math.round(routeStats.time_min)} mins</div>
          <div>Risk: {(routeStats.risk * 100).toFixed(0)}%</div>
        </div>
      )}
    </div>
  );
}

export default Sidebar;
